/*Table structure for table `goods2` */

DROP TABLE IF EXISTS `goods2`;

CREATE TABLE `goods2` (
  `id` int(11) NOT NULL DEFAULT '0',
  `name` varchar(45) NOT NULL DEFAULT '0' COMMENT '物品名称',
  `type` int(11) NOT NULL DEFAULT '1' COMMENT '1.竞技任务刷新次数 2.金币 3.每日特惠商店刷新次数 4.PK每日商店刷新次数 5.挑战每日商店刷新次数 6.补签消耗类 7.极品夺宝费用 8.改造页购买 11.工会增员费用',
  `value` int(11) NOT NULL DEFAULT '0' COMMENT '数值',
  `buy_count` int(11) NOT NULL DEFAULT '1' COMMENT '第几次购买',
  `price_type` int(11) NOT NULL DEFAULT '1' COMMENT '价格类型0=免费，1=金币，2=钻石，3=点券，4=兵团荣誉点数，5=零件碎片',
  `price` int(11) NOT NULL DEFAULT '1' COMMENT '价格',
  `need_vip_lv` int(11) DEFAULT '0' COMMENT '需要的最低vip等级',
  PRIMARY KEY (`id`)
