/*Table structure for table `guide` */

DROP TABLE IF EXISTS `guide`;

CREATE TABLE `guide` (
  `id` int(11) unsigned NOT NULL COMMENT '系统编号',
  `guide_type` int(11) NOT NULL DEFAULT '0' COMMENT '功能引导的类型：1=改名，2=操作练习、3=邮箱、4=商城、5=角色配置、6=实战训练、7=任务、8=在线奖励、9=等级礼包、10=对战、11=排位、12=挑战、14=成就、15= 枪械升级、16=枪械部件升级、17=装备升级、18=枪械进阶',
  `step` int(11) NOT NULL DEFAULT '0' COMMENT '功能引导进行的步骤',
  `step_intro` varchar(200) NOT NULL DEFAULT '0' COMMENT '步骤描述',
  `condition1_type` int(11) NOT NULL DEFAULT '0' COMMENT '触发条件类型：0=上一步骤完成、1=上一个引导类型的所有步骤完成、2=角色等级、3=玩家自行点击对应按钮、4=命名完成后、5=战斗结束后',
  `condition1` int(11) NOT NULL DEFAULT '0' COMMENT 'condition1_type=1时该处表示上一个引导的type、condition1_type=2时该处表示角色的等级、condition1_type=3时该处表示按钮名称（1=枪械升级界面的枪械部件图标，2=角色配置界面的天赋按钮，3=角色配置界面的装备图标，4=角色升级提示界面的确定按钮）、condition1_type=4和5时该处默认0',
  `condition2_type` int(11) NOT NULL DEFAULT '0',
  `condition2` int(11) NOT NULL DEFAULT '0' COMMENT '触发条件2',
  `condition3_type` int(11) NOT NULL DEFAULT '0',
  `condition3` int(11) NOT NULL DEFAULT '0' COMMENT '触发条件3',
  `record` int(11) NOT NULL DEFAULT '0' COMMENT '服务端该步骤记录（如果填0表示不记录，填的值如果大于总步骤数，则表示到该步时引导就完结；如果填的值刚好为步骤顺序，则表示该步骤需要记录。）',
  `awards` varchar(200) NOT NULL DEFAULT '0' COMMENT '奖励，用如下形式进行表示：type,id,amount;type,id,amount;type,id,amount;',
  PRIMARY KEY (`id`)
