/*Table structure for table `gun` */

DROP TABLE IF EXISTS `gun`;

CREATE TABLE `gun` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '枪械/部件id',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '枪械名',
  `img_id` int(10) NOT NULL DEFAULT '0' COMMENT '图片id该ID同时作为是否同把枪械的判断条件。有特殊同图片枪械，需要新增id记录。',
  `gun_effect_id` int(10) NOT NULL DEFAULT '0' COMMENT '枪械3d模型ID',
  `intro` varchar(1000) NOT NULL DEFAULT '0' COMMENT '介绍',
  `intro2` varchar(200) NOT NULL DEFAULT '0' COMMENT '介绍2',
  `intro3` varchar(200) NOT NULL DEFAULT '0' COMMENT '介绍3',
  `type` int(10) NOT NULL DEFAULT '0' COMMENT '类型1:步枪,2:机枪,3:狙击,4:霰弹,5:喷火枪,6:十字弩,7电磁枪，8榴弹炮',
  `dam` float NOT NULL DEFAULT '0' COMMENT '伤害',
  `dam_inc` float NOT NULL DEFAULT '0' COMMENT '伤害成长',
  `pass_dam` float NOT NULL DEFAULT '0' COMMENT '穿透',
  `pass_dam_inc` float NOT NULL DEFAULT '0' COMMENT '穿透成长',
  `crit` float NOT NULL DEFAULT '0' COMMENT '暴击',
  `crit_inc` float NOT NULL DEFAULT '0' COMMENT '暴击成长',
  `fire_spd` float NOT NULL DEFAULT '0' COMMENT '射速',
  `fire_spd_inc` float NOT NULL DEFAULT '0' COMMENT '射速成长',
  `box_magazine` float NOT NULL DEFAULT '0' COMMENT '弹夹',
  `box_magazine_inc` float NOT NULL DEFAULT '0' COMMENT '弹夹成长',
  `accu` float NOT NULL DEFAULT '0' COMMENT '精准',
  `accu_inc` float NOT NULL DEFAULT '0' COMMENT '精准成长',
  `recoil` float NOT NULL DEFAULT '0' COMMENT '稳定性',
  `recoil_inc` float NOT NULL DEFAULT '0' COMMENT '稳定性成长',
  `range` float NOT NULL DEFAULT '0' COMMENT '射程',
  `range_inc` float NOT NULL DEFAULT '0' COMMENT '射程成长',
  `reload` float NOT NULL DEFAULT '0' COMMENT '填弹时间',
  `reload_inc` float NOT NULL DEFAULT '0' COMMENT '填弹成长',
  `armor_pass` float NOT NULL COMMENT '护甲穿透',
  `armor_pass_inc` float NOT NULL COMMENT '护甲穿透成长值',
  `blast_range` float NOT NULL DEFAULT '0' COMMENT '该枪械的爆炸范围，是指弹道打中或者结束时，是否会产生一个对周围爆炸的受击范围',
  `blast_param` float NOT NULL COMMENT '爆炸判定参数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

