/*Table structure for table `gun_effect` */

DROP TABLE IF EXISTS `gun_effect`;

CREATE TABLE `gun_effect` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '枪械外观ID',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '枪械名称',
  `prefab_id` int(50) NOT NULL DEFAULT '0' COMMENT '模型名字',
  `action` int(10) NOT NULL DEFAULT '0' COMMENT '动作前缀（1:Rifles，2:Gun，3：Lens，4：Shotgun，5：Flat，6：Double，7：Fire，8：Crossbow，9：Electric，10：Shrapnel）',
  `panning_top_tolerance` int(50) NOT NULL DEFAULT '0' COMMENT '射击向上偏移量',
  `panning_down_tolerance` int(50) NOT NULL DEFAULT '0' COMMENT '射击向下偏移量',
  `panning_left_tolerance` int(50) NOT NULL DEFAULT '0' COMMENT '射击向左偏移量',
  `panning_right_tolerance` int(50) NOT NULL DEFAULT '0' COMMENT '射击向右偏移量',
  `panning_angle_tolerance` int(50) NOT NULL DEFAULT '0' COMMENT '射击偏移角度允许值',
  `panning_delay` int(50) NOT NULL DEFAULT '0' COMMENT '射击偏移延迟',
  `panning_tracing_speed` int(50) NOT NULL DEFAULT '0' COMMENT '射击偏移追随速度',
  `panning_return_speed` int(50) NOT NULL DEFAULT '0' COMMENT '射击偏移返回速度',
  `hit_shake_amplitude` float NOT NULL DEFAULT '0' COMMENT '射击震屏幅度',
  `hit_shake_duration` float NOT NULL DEFAULT '0' COMMENT '射击震屏时间',
  `rotation_forui` varchar(500) NOT NULL DEFAULT '0' COMMENT 'UI武器展示位置旋转',
  `barycentre_deviation` varchar(500) NOT NULL DEFAULT '0' COMMENT '重心偏移',
  `muzzle` int(10) NOT NULL DEFAULT '0' COMMENT '枪口特效',
  `ballistic` int(10) NOT NULL DEFAULT '0' COMMENT '弹道特效',
  `bullet` int(10) NOT NULL DEFAULT '0' COMMENT '子弹特效',
  `shellcase` int(10) NOT NULL DEFAULT '0' COMMENT '弹壳特效',
  `fire_angle` int(10) NOT NULL DEFAULT '0' COMMENT '射击夹角值（超过这个值不开枪）',
  `projectile` int(10) NOT NULL DEFAULT '0' COMMENT '枪械弹道',
  `dummy` int(10) NOT NULL DEFAULT '2' COMMENT '描述：武器要挂在哪个挂点上。挂点位置。1左手，2右手，3左脚，4右脚，5左肩，6右肩，7左胯部，8右胯部，9胸，10头，11枪身，12枪口，13原点',
  `reload_performance` int(10) NOT NULL DEFAULT '0' COMMENT '填弹表现，索引到performance',
  PRIMARY KEY (`id`)
