/*Table structure for table `head_frame` */

DROP TABLE IF EXISTS `head_frame`;

CREATE TABLE `head_frame` (
  `id` int(11) NOT NULL COMMENT '唯一识别符',
  `name` varchar(100) NOT NULL COMMENT '头像框名称',
  `img_id` int(11) NOT NULL COMMENT '对应的图标名称',
  `condition_word` varchar(100) NOT NULL COMMENT '头像框解锁条件描述',
  `classification` int(10) NOT NULL COMMENT '1=其它，2=等级，3=排位',
  `unlock_type` int(10) NOT NULL COMMENT '解锁类型 1=直接解锁，2=等级，3=排位段位',
  `unlock_type_parameter` varchar(64) NOT NULL DEFAULT '' COMMENT '解锁类型的条件参数，等级=玩家等级，排位=排位等级（1,2=青铜2级）',
  `quality` int(10) NOT NULL COMMENT '头像框的品质',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

