/*Table structure for table `hero` */

DROP TABLE IF EXISTS `hero`;

CREATE TABLE `hero` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '英雄id',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '名字',
  `sex` int(10) NOT NULL COMMENT '性别:1=男性;2=女性',
  `body_radius` float unsigned NOT NULL DEFAULT '0.5' COMMENT '角色的命中范围判定半径。单位米',
  `fire_deceleration` float unsigned NOT NULL DEFAULT '0.5' COMMENT '配置数值多少，射击时的速度比例就是多少。比如填0.4就表示射击时的速度是正常的0.4速度。',
  `hero_type` int(10) unsigned NOT NULL COMMENT '英雄类型',
  `double_gun` int(10) unsigned NOT NULL COMMENT '是否双枪英雄:1或0',
  `quality` int(10) NOT NULL DEFAULT '6' COMMENT '品质1白色2、绿色3、蓝色4紫色,5橙色',
  `gun_id` int(10) NOT NULL COMMENT '英雄所使用的枪械',
  `default_skin` int(10) NOT NULL DEFAULT '0' COMMENT '英雄默认皮肤',
  `intro` varchar(1000) NOT NULL DEFAULT '0' COMMENT '介绍',
  `intro2` varchar(500) NOT NULL DEFAULT '0' COMMENT '短介绍',
  `intro3` varchar(100) NOT NULL DEFAULT '0' COMMENT '定位',
  `intro4` varchar(100) NOT NULL DEFAULT '0' COMMENT '特点',
  `icon` varchar(10) NOT NULL DEFAULT '0' COMMENT '图标',
  `shape_id` int(10) NOT NULL DEFAULT '0' COMMENT '3D人物模型ID',
  `skill` int(10) NOT NULL DEFAULT '0' COMMENT '英雄技能',
  `spd` float NOT NULL DEFAULT '0' COMMENT '机动性',
  `spd_up` float NOT NULL DEFAULT '0' COMMENT '机动性提升',
  `hp` float NOT NULL DEFAULT '0' COMMENT '生命值',
  `hp_up` float NOT NULL DEFAULT '0' COMMENT '生命值提升',
  `def` float NOT NULL DEFAULT '0' COMMENT '护甲值',
  `def_up` float NOT NULL DEFAULT '0' COMMENT '护甲值提升',
  `crit` float NOT NULL DEFAULT '0' COMMENT '暴击率',
  `crit_up` float NOT NULL DEFAULT '0' COMMENT '暴击率提升',
  `inte` float NOT NULL DEFAULT '0' COMMENT '道具强度',
  `inte_up` float NOT NULL DEFAULT '0' COMMENT '道具强度提升',
  `angle_velocity` int(10) NOT NULL DEFAULT '0' COMMENT '描述：填0，表示不需要走旋转逻辑。填角度则需要转身锁定敌人时，的旋转角速度。单位度/秒',
  `vehicle_time` int(10) NOT NULL DEFAULT '0' COMMENT '存在时间，单位秒。0表示永远存在',
  `viability` float NOT NULL DEFAULT '0' COMMENT '生存能力（用百分比，到时候策划只填一个值，比如50，下同）',
  `atk_ability` float NOT NULL DEFAULT '0' COMMENT '攻击伤害能力',
  `skill_ability` float NOT NULL DEFAULT '0' COMMENT '技能效果',
  `operate_difficulty` float NOT NULL DEFAULT '0' COMMENT '上手难度',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

