/*Table structure for table `map` */

DROP TABLE IF EXISTS `map`;

CREATE TABLE `map` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '系统编号id',
  `map_id` int(10) NOT NULL DEFAULT '0' COMMENT '地图id',
  `map_entities_group_id` int(10) NOT NULL DEFAULT '0' COMMENT '对应场景实体id',
  `map_name` varchar(50) NOT NULL DEFAULT '0' COMMENT '地图名',
  `info` varchar(100) NOT NULL DEFAULT '0' COMMENT '地图描述信息',
  `fight_type` int(10) NOT NULL DEFAULT '1' COMMENT '战斗类型：1=对战，2=排位，3=混战，4=新手引导 5=团队混战 6=Boss模式（对应line_mode的id） 7=兵团对战 8=兵团混战 9=新手混战 10=新手对战',
  `open_lv` int(10) DEFAULT NULL COMMENT '战斗类型及模式的开放等级',
  `mode_type` int(10) NOT NULL DEFAULT '0' COMMENT '战斗模式：1=团队模式，2=歼灭战模式，3=人质解救模式，4=爆破模式，5=资源模式，6=占领模式，7=手雷战模式、8=狙击战模式，10=混战模式，11=新手模式，12=新手引导，13=组队混战，20=BOSS模式，21=BOSS引导模式，99=测试模式',
  `mode_name` varchar(100) NOT NULL DEFAULT '0' COMMENT '模式名称',
  `io_item` int(50) NOT NULL DEFAULT '0' COMMENT '表示这张地图是否有IO掉落物品，填0表示不掉落，索引到triggers表。掉落物品的位置从map_generate表读',
  `bomb_camp` int(10) NOT NULL DEFAULT '0' COMMENT '如果爆炸模式，埋弹的阵营。1为警察，2为土匪。',
  `bomb_area` varchar(100) NOT NULL DEFAULT '0,0,0' COMMENT '如果爆炸模式，炸弹的生成点',
  `min_round` int(10) NOT NULL DEFAULT '1' COMMENT '对局局数最小值（只能选奇数，1=1局1胜，3=3局2胜，5=5局3胜，依次类推）',
  `max_round` int(10) NOT NULL DEFAULT '1' COMMENT '对局局数最大值（只能选奇数，1=1局1胜，3=3局2胜，5=5局3胜，依次类推）',
  `match_round` int(10) NOT NULL DEFAULT '1' COMMENT '匹配时，系统创建的房间所选择的局数（如果匹配到玩家创建的房间，则用玩家所选择的局数）',
  `min_k_num` int(10) NOT NULL DEFAULT '10' COMMENT '如是人头模式或者可复活人头限定多人混战模式，还应有击杀人头判定值最小值。对战模式下玩家可自行选择，排位模式下系统固定',
  `max_k_num` int(10) NOT NULL DEFAULT '0' COMMENT '如是人头模式或者可复活人头限定多人混战模式，还应有击杀人头判定值最大值。对战模式下玩家可自行选择，排位模式下系统固定',
  `match_k_num` int(10) NOT NULL DEFAULT '20' COMMENT '匹配时，系统创建的房间所选择的人头数（如果匹配到玩家创建的房间，则用玩家所选择的人头数）',
  `limit_users` int(10) NOT NULL DEFAULT '0' COMMENT '房间人数上限',
  `exist_sec` varchar(50) NOT NULL DEFAULT '0' COMMENT '单局战斗最长持续时间（为0表示无限制）中间以逗号隔开，则表示可以由时间配置选择多级。在一些限时模式中会用到。',
  `relive_time` int(10) NOT NULL DEFAULT '0' COMMENT '如是人头模式/团队模式，还应有单人初始复活时间',
  `add_time` int(10) NOT NULL DEFAULT '0' COMMENT '如是人头模式/团队模式，还应有单人死亡一次增加的复活',
  `limit_rel_time` int(10) NOT NULL DEFAULT '0' COMMENT '如是人头模式/团队模式，还应有单人复活时间上限',
  `bgm` varchar(20) NOT NULL DEFAULT '0' COMMENT '索引到音效表',
  `bomb_link_pos` varchar(1000) NOT NULL DEFAULT '0' COMMENT '爆破区域连接点',
  `camp_amount` int(11) NOT NULL DEFAULT '1' COMMENT '阵营数量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

