/*Table structure for table `npc_hero` */

DROP TABLE IF EXISTS `npc_hero`;

CREATE TABLE `npc_hero` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '英雄id',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '名字',
  `intro` varchar(50) NOT NULL DEFAULT '0' COMMENT '介绍',
  `icon` varchar(10) NOT NULL DEFAULT '0' COMMENT '图标',
  `double_gun` int(10) NOT NULL DEFAULT '0',
  `npc_type` int(10) NOT NULL DEFAULT '0' COMMENT 'NPC类型1：机器人2：炮台：3：BOSS：',
  `hero_skill_id` int(10) NOT NULL DEFAULT '0' COMMENT '关联至hero_skill的ID',
  `AI_type` int(10) NOT NULL DEFAULT '0' COMMENT 'AI种类1：被动怪2：不动怪',
  `camp` int(10) NOT NULL DEFAULT '0' COMMENT '阵营区分：1、中立。2A（我）方。3B方',
  `shape_id` int(10) NOT NULL DEFAULT '0' COMMENT '3D人物模型ID',
  `body_radius` float unsigned NOT NULL DEFAULT '0.5' COMMENT '角色的命中范围判定半径。单位米',
  `fire_deceleration` float unsigned NOT NULL DEFAULT '0.5' COMMENT '配置数值多少，射击时的速度比例就是多少。比如填0.4就表示射击时的速度是正常的0.4速度。',
  `spy_range` int(10) NOT NULL DEFAULT '0' COMMENT '索敌范围',
  `follow_range` int(10) NOT NULL DEFAULT '0' COMMENT '追击范围',
  `spd` float NOT NULL DEFAULT '0' COMMENT '机动性',
  `sta` float NOT NULL DEFAULT '0' COMMENT '耐力',
  `hp` float NOT NULL DEFAULT '0' COMMENT '生命值',
  `atk` float NOT NULL DEFAULT '0' COMMENT '攻击力',
  `def` float NOT NULL DEFAULT '0' COMMENT '护甲值',
  `crit` float NOT NULL DEFAULT '0' COMMENT '暴击率',
  `a_crit` float NOT NULL DEFAULT '0' COMMENT '防暴击率',
  `cspd` float NOT NULL DEFAULT '0' COMMENT '换弹加速',
  `inte` float NOT NULL DEFAULT '0' COMMENT '道具强度',
  `cry` float NOT NULL DEFAULT '0' COMMENT '负载',
  `use_weapon1` int(11) NOT NULL DEFAULT '0' COMMENT '使用武器1',
  `use_weapon2` int(11) NOT NULL DEFAULT '0' COMMENT '使用武器2',
  `use_item` int(11) NOT NULL DEFAULT '0' COMMENT '使用道具',
  `item_count` int(11) NOT NULL DEFAULT '0' COMMENT '道具数量',
  `brush_performance` int(20) NOT NULL DEFAULT '0' COMMENT '刷出表现',
  `brush_time` int(20) NOT NULL DEFAULT '0' COMMENT '刷出时间',
  `vehicle_time` int(20) NOT NULL DEFAULT '0' COMMENT '存在时间，单位秒。0表示永远存在',
  `collision_type` int(10) NOT NULL DEFAULT '0' COMMENT '是否阻挡(填1人阻挡，填2子弹阻挡，3=1+2，填4表示手雷阻挡。填0不阻挡)',
  `angle_velocity` int(10) NOT NULL DEFAULT '0' COMMENT '描述：填0，表示不需要走旋转逻辑。填角度则需要转身锁定敌人时，的旋转角速度。单位度/秒',
  `follow_dead` int(10) NOT NULL DEFAULT '0' COMMENT '是否跟随死亡。1表示是，0表示否',
  `limit_num` int(10) NOT NULL DEFAULT '0' COMMENT '限定个数。超过最大上限，则最先放下的NPC会被销毁',
  `die_performance` int(10) NOT NULL DEFAULT '0' COMMENT '怪物的死亡表现，索引到performance表',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

