/*Table structure for table `npc_weapons` */

DROP TABLE IF EXISTS `npc_weapons`;

CREATE TABLE `npc_weapons` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '枪械/部件id',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '枪械名',
  `img_id` int(10) NOT NULL DEFAULT '0' COMMENT '图片id该ID同时作为是否同把枪械的判断条件。有特殊同图片枪械，需要新增id记录。',
  `gun_effect_id` int(10) NOT NULL DEFAULT '0' COMMENT '枪械3d模型ID',
  `type` int(10) NOT NULL DEFAULT '0' COMMENT '类型1:步枪2:机枪3：狙击4：散弹',
  `dam` float NOT NULL DEFAULT '0' COMMENT '伤害',
  `pass_dam` float NOT NULL DEFAULT '0' COMMENT '穿透',
  `crit` float NOT NULL DEFAULT '0' COMMENT '暴击',
  `fire_spd` float NOT NULL DEFAULT '0' COMMENT '射速',
  `box_magazine` float NOT NULL DEFAULT '0' COMMENT '弹匣',
  `accu` float NOT NULL DEFAULT '0' COMMENT '精准',
  `recoil` float NOT NULL DEFAULT '0' COMMENT '稳定性',
  `range` float NOT NULL DEFAULT '0' COMMENT '射程',
  `reload` float NOT NULL DEFAULT '0' COMMENT '填弹',
  `weight` float NOT NULL DEFAULT '0' COMMENT '重量',
  `armor_pass` float NOT NULL COMMENT '护甲穿透',
  `ai_hit_rate` int(11) NOT NULL DEFAULT '100' COMMENT 'ai命中百分比',
  `blast_range` float NOT NULL DEFAULT '0' COMMENT '该枪械的爆炸范围，是指弹道打中或者结束时，是否会产生一个对周围爆炸的受击范围',
  `blast_param` float NOT NULL DEFAULT '0' COMMENT '爆炸判定参数',
  PRIMARY KEY (`id`)
