/*Table structure for table `projectile` */

DROP TABLE IF EXISTS `projectile`;

CREATE TABLE `projectile` (
  `id` int(10) NOT NULL COMMENT 'id',
  `name` varchar(20) NOT NULL COMMENT '弹道名称',
  `performance` int(10) NOT NULL COMMENT '弹道表现',
  `hit_performance` int(10) NOT NULL DEFAULT '0' COMMENT '弹道的受击表现，索引到performance表。',
  `hit_role_performance` int(10) NOT NULL DEFAULT '0' COMMENT '角色受击表现',
  `impulse` float NOT NULL COMMENT '弹道力度',
  `speed` float NOT NULL COMMENT '弹道速度',
  `projectile_type` int(10) NOT NULL COMMENT '弹道类型（0：bullet，1：步枪，2、机枪,4、散弹，5、狙击，6、手雷，7、闪光，8、普通直线，9、普通抛物线，10、导弹，11、抓钩，12、喷火枪,13、追踪弹）',
  `project_launch_angle` float NOT NULL COMMENT '弹道初始发射角度',
  `projectile_launch_direction` int(10) NOT NULL COMMENT '弹道方向（0=瞄准方向，1=枪口的方向 2=目标方向）',
  `project_width` int(10) NOT NULL COMMENT '弹道宽度',
  `collision_type` varchar(20) NOT NULL COMMENT '是否阻挡(索引到scene_layer)',
  `through_performance` int(10) NOT NULL COMMENT '穿透表现',
  `dummy` int(11) NOT NULL COMMENT '挂点位置。1左手，2右手，3左脚，4右脚，5左肩，6右肩，7左胯部，8右胯部，9胸，10头，11枪身，12枪口，13原点，',
  `trigger_type` varchar(20) NOT NULL COMMENT '是否触发(索引到scene_layer)',
  `end_performance` int(10) NOT NULL DEFAULT '0' COMMENT '弹道结束表现，配置爆炸表现，索引到performance表',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';

