/*Table structure for table `skill` */

DROP TABLE IF EXISTS `skill`;

CREATE TABLE `skill` (
  `id` int(100) NOT NULL COMMENT 'id',
  `skill_id` int(10) NOT NULL DEFAULT '0' COMMENT '技能ID',
  `lv` int(10) NOT NULL DEFAULT '0' COMMENT '技能等级',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '技能名称',
  `intro` varchar(150) NOT NULL DEFAULT '0' COMMENT '介绍',
  `intro2` varchar(150) DEFAULT '0' COMMENT '介绍2',
  `intro3` varchar(150) DEFAULT '0' COMMENT '介绍3',
  `img_id` int(10) NOT NULL DEFAULT '0' COMMENT '图片id',
  `skill_performance_id` int(10) NOT NULL DEFAULT '0' COMMENT '技能表现ID（索引到skill_performance）',
  `type` int(10) NOT NULL DEFAULT '0' COMMENT '0=被动，1=普通主动技能，2=行为技能，（1不走skill_action表）',
  `talent_id` int(10) NOT NULL DEFAULT '0' COMMENT '天赋id',
  `skill_action_id` int(20) NOT NULL DEFAULT '0' COMMENT '技能行为ID，索引到skill_action表',
  `group_id` int(20) NOT NULL DEFAULT '0' COMMENT '连招分组ID',
  `group_step` int(20) NOT NULL DEFAULT '0' COMMENT '连招阶段',
  `next_time` int(10) NOT NULL DEFAULT '0' COMMENT '连招响应结束时间',
  `charge` int(10) NOT NULL DEFAULT '0' COMMENT '充能次数上限。为0时表示非充能技能，大于0时，为充能次数上限',
  `category` int(10) NOT NULL DEFAULT '0' COMMENT '0：点击释放，1：点击选定目标释放，2：偏移直线释放，3：偏移范围释放4：偏移扇形释放',
  `arng` int(10) NOT NULL DEFAULT '0' COMMENT '0：自身1：指定目标位置2：单体目标3：扇形目标4：范围目标5：直线目标6：触发器7：弹道方向8：弹道目标点9扇形随机目标10十字交叉斩',
  `target_camp` int(10) NOT NULL DEFAULT '0' COMMENT '目标阵营。0：无视敌我。1我方。2敌方',
  `special_type` int(10) NOT NULL DEFAULT '0' COMMENT '1=手雷爆炸，中心最高，边缘递减',
  `special_type_param` int(10) NOT NULL DEFAULT '0' COMMENT '特殊伤害机制参数',
  `cd_time` int(10) NOT NULL DEFAULT '0' COMMENT '技能冷却时间',
  `var_1_effect` int(10) NOT NULL DEFAULT '0' COMMENT 'Effect类型',
  `var_2_effect` int(10) NOT NULL DEFAULT '0' COMMENT 'Effect类型',
  `var_3_effect` int(10) NOT NULL DEFAULT '0' COMMENT 'Effect类型',
  `effect_delay` int(10) NOT NULL DEFAULT '0' COMMENT '实际效果延迟时间，以skill_acion的生效时间开始计算。',
  `effect_times` int(10) NOT NULL DEFAULT '0' COMMENT '效果次数',
  `mutieffect_time` int(10) NOT NULL DEFAULT '0' COMMENT '如技能造成多次效果，还应有后一次伤害距离前一次效果的时间',
  `target_range` float NOT NULL DEFAULT '0' COMMENT '作用目标点最大距离',
  `target_angle` float NOT NULL DEFAULT '0' COMMENT '技能作用的扇形区域角度',
  `target_area` float NOT NULL DEFAULT '0' COMMENT '作用范围半径',
  `target_width` float NOT NULL DEFAULT '0' COMMENT '作用宽度',
  `run_away_del` int(10) NOT NULL DEFAULT '0' COMMENT '离开是否删除buff0=不删除，1删除',
  `deviation_x` float NOT NULL DEFAULT '0' COMMENT '技能释放时，相对中心点的X轴偏移量',
  `deviation_z` float NOT NULL DEFAULT '0' COMMENT '技能释放时，相对中心点的Z轴偏移量，Z是角色方向',
  `deviation_rotation` float NOT NULL DEFAULT '0' COMMENT '技能释放时，相对中心点产生的旋转值',
  `group_auto` int(11) NOT NULL DEFAULT '0' COMMENT '0表示不自动 1表示自动',
  PRIMARY KEY (`skill_id`,`lv`),
  UNIQUE KEY `skill_id` (`id`)
