/*Table structure for table `skill_action` */

DROP TABLE IF EXISTS `skill_action`;

CREATE TABLE `skill_action` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT 'ID',
  `name` char(50) NOT NULL DEFAULT '0' COMMENT '技能名字',
  `type` int(10) NOT NULL DEFAULT '0' COMMENT '技能分类（1=基础动作（只包含移动），2=普通攻击(包含抬枪)，3=填弹，4=模式技能，5=物品技能，6=通用技能，7=技能1，8=技能2，9=技能3,20=被击杀',
  `startup_time` int(10) NOT NULL DEFAULT '0' COMMENT '前摇时间',
  `startup_interrupt` varchar(50) NOT NULL DEFAULT '0' COMMENT '前摇打断ID，索引type类型，数组，0表示可以被任何操作打断，-1表示不能被任何打断。',
  `active_time` int(10) NOT NULL DEFAULT '0' COMMENT '生效时间',
  `active_interrupt` varchar(50) NOT NULL DEFAULT '-1' COMMENT '生效打断ID，索引type类型，数组，0表示可以被任何操作打断，-1表示不能被任何打断。',
  `active_move` int(10) NOT NULL DEFAULT '0' COMMENT '生效期间是否可移动，0不能，1可以',
  `active_rotate` int(10) NOT NULL DEFAULT '0' COMMENT '生效期间是否可转向，0不能，1可以',
  `recover_time` int(10) NOT NULL DEFAULT '0' COMMENT '后摇时间',
  `recover_interrupt` varchar(50) NOT NULL DEFAULT '0' COMMENT '后摇打断ID，索引type类型，数组，0表示可以被任何操作打断，-1表示不能被任何打断。',
  `button_time` int(10) NOT NULL DEFAULT '0' COMMENT '按钮生效开始时间。',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';

