/*Table structure for table `skill_effect_interrupt` */

DROP TABLE IF EXISTS `skill_effect_interrupt`;

CREATE TABLE `skill_effect_interrupt` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT '打断效果ID',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '名字',
  `interrupt_id` varchar(50) NOT NULL DEFAULT '0' COMMENT '打断ID，填打断类型数组技能分类（1=基础动作（包含移动、抬枪），2=普通攻击，3=填弹，4=模式技能，5=物品技能，6=通用技能，7=技能1，8=技能2，9=技能3',
  `interrupt_active` int(10) NOT NULL DEFAULT '0' COMMENT '是否打断生效时间，0不打断，1打断',
  `imprison` varchar(50) NOT NULL DEFAULT '0' COMMENT '效果禁用动作id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';

