/*Table structure for table `skill_performance` */

DROP TABLE IF EXISTS `skill_performance`;

CREATE TABLE `skill_performance` (
  `id` int(10) NOT NULL DEFAULT '0' COMMENT 'id',
  `type` int(10) NOT NULL DEFAULT '0' COMMENT '1=一段表现，2=二段表现，3=三段表现，4=四段表现',
  `name` varchar(50) NOT NULL DEFAULT '0' COMMENT '名字',
  `performance_type_1` int(10) NOT NULL DEFAULT '0' COMMENT '一段表现类型',
  `performance_1` int(10) NOT NULL DEFAULT '0' COMMENT '一段表现ID（索引到performance）',
  `performance_loop_1` int(10) NOT NULL DEFAULT '0' COMMENT '一段表现是否循环，（0不循环，1循环。）循环的时候，对应时间不生效。',
  `performance_type_2` int(10) NOT NULL DEFAULT '0' COMMENT '二段表现类型',
  `performance_2` int(10) NOT NULL DEFAULT '0' COMMENT '二段表现ID（索引到performance）',
  `performance_loop_2` int(10) NOT NULL DEFAULT '0' COMMENT '二段表现是否循环，（0不循环，1循环。）循环的时候，对应时间不生效。',
  `performance_type_3` int(10) NOT NULL DEFAULT '0' COMMENT '三段表现类型',
  `performance_3` int(10) NOT NULL DEFAULT '0' COMMENT '三段表现ID（索引到performance）',
  `performance_loop_3` int(10) NOT NULL DEFAULT '0' COMMENT '三段表现是否循环',
  `performance_type_4` int(10) NOT NULL DEFAULT '0' COMMENT '四段表现类型',
  `performance_4` int(10) NOT NULL DEFAULT '0' COMMENT '四段表现ID',
  `performance_loop_4` int(10) NOT NULL DEFAULT '0' COMMENT '四段表现是否循环',
  `separate` int(10) NOT NULL DEFAULT '0' COMMENT '表示这个技能特效是否应用上下半身分离动作，填0为全身动作，填1上下半身分离。',
  `cast_performance` int(10) NOT NULL DEFAULT '0' COMMENT '回调事件播放表现',
  `projectile` int(10) NOT NULL DEFAULT '0' COMMENT '弹道表现',
  `delay_performance` int(10) NOT NULL DEFAULT '0' COMMENT '延迟触发效果表现',
  `delay_time` int(10) NOT NULL DEFAULT '0' COMMENT '填0表示不延迟，填其他值表示在时间回调之后延迟多久去播放delay_performance',
  `weapon_performance` int(10) NOT NULL DEFAULT '0' COMMENT '武器表现。填-1，只隐藏原来武器，不替换新武器上去。填0，不隐藏原武器，不替换新武器。填ID索引performance表，隐藏原武器，播放新表现',
