/*Table structure for table `triggers` */

DROP TABLE IF EXISTS `triggers`;

CREATE TABLE `triggers` (
  `trigger_id` int(11) NOT NULL DEFAULT '0' COMMENT '触发器ID',
  `name` char(50) NOT NULL DEFAULT '0' COMMENT '触发器名称',
  `rotating_y` float NOT NULL DEFAULT '0' COMMENT '方向信息，旋转Y值',
  `collision_length` float NOT NULL DEFAULT '0' COMMENT '碰撞长度',
  `collision_width` float NOT NULL DEFAULT '0' COMMENT '碰撞宽度',
  `trigger_radius` char(50) NOT NULL DEFAULT '0' COMMENT '触发范围（填1个为半径，填两个值，第一个值为X值，第二个值为Z值。触发器方向为Z轴。）',
  `anchor_x` float NOT NULL DEFAULT '0.5' COMMENT '触发器的坐标点在触发范围里的X轴比例值',
  `anchor_z` float NOT NULL DEFAULT '0.5' COMMENT '触发器的坐标点在触发范围里的Z轴比例值',
  `cd_time` varchar(100) NOT NULL DEFAULT '0' COMMENT '冷却时间：单个值则表示直接取该值；如果是t1,t2表示从这个区间中随机取一个值。',
  `trigger_delay` int(50) NOT NULL DEFAULT '0' COMMENT '触发延迟',
  `trigger_times` int(11) NOT NULL DEFAULT '0' COMMENT '触发次数',
  `trigger_destroyed` int(10) NOT NULL DEFAULT '1' COMMENT '表示这个触发器，在触发次数达到上限时，是否立即销毁。填1立即销毁，填0不立即销毁。填0的时候需要等destroy_time达到时才销毁',
  `contact_type` int(11) NOT NULL DEFAULT '0' COMMENT '激活判定（填1表示人触发，填2表示子弹触发，填4伤害触发，填8客户端人触发，填32时间触发，）',
  `collision_type` int(11) NOT NULL DEFAULT '0' COMMENT '是否阻挡(填1人阻挡，填2子弹阻挡，3=1+2，填4表示手雷阻挡。填0不阻挡)',
  `trigger_type` int(50) NOT NULL DEFAULT '0' COMMENT '触发器类型（1表示skill，2表示触发器,3=表示爆炸区（无），4=表示C4区（无），5=炸药区域，6=表示临时资源点，7=固定资源点,8=远离持续触发器,9=草丛,10=载具11=远离即取消触发器',
  `para_id` int(50) NOT NULL DEFAULT '0' COMMENT '相关ID',
  `skill_lv` int(50) NOT NULL DEFAULT '0' COMMENT '技能等级',
  `is_destroyed` int(50) NOT NULL DEFAULT '0' COMMENT '是否销毁',
  `destroy_time` int(11) NOT NULL DEFAULT '0' COMMENT '自销毁时间。0表示无限时',
  `destroy_skill` int(11) NOT NULL DEFAULT '0' COMMENT '销毁触发技能',
  `destroy_skill_lv` int(11) NOT NULL DEFAULT '0' COMMENT '销毁触发技能等级',
  `trigger_multiple` int(50) NOT NULL DEFAULT '1' COMMENT '同时生成触发器个数.不限个数为-1',
  `refresh_time` varchar(50) NOT NULL DEFAULT '0' COMMENT '随机刷新时间区间单位MS。10,20表示时间为10MS到20MS。',
  `model_next_hp` int(10) NOT NULL DEFAULT '0' COMMENT '下一阶模型生命值',
  `hp` int(10) NOT NULL DEFAULT '0' COMMENT '生命值',
  `pass_param` int(10) NOT NULL DEFAULT '0' COMMENT '穿透率，数值1表示1%。该值可为负数',
  `follow_dead` int(10) NOT NULL DEFAULT '0' COMMENT '是否跟随死亡。1表示是，0表示否',
  `limit_num` int(10) NOT NULL DEFAULT '0' COMMENT '限定个数。超过最大上限，则最先放下的NPC会被销毁',
  `animator` int(10) NOT NULL DEFAULT '0' COMMENT '是否需要动画状态机:0不需要, 1需要',
  `exist_num` int(10) NOT NULL DEFAULT '0' COMMENT '触发器同时存在的最大数量',
  `brushwood_visible` int(11) NOT NULL DEFAULT '0' COMMENT '表示草丛可见。0为不可见。1为可见',
  `brush_time` int(10) NOT NULL DEFAULT '0' COMMENT '刷出时间，在刷出时间内触发器不会进行触发。\r',
  `warning_delay` int(10) NOT NULL DEFAULT '0' COMMENT '刷出前进行警示的时间。0表示不需要警示',
  PRIMARY KEY (`trigger_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';

